INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (78, 'Nice', 1, 'De geelvoorhoofdamazone is een papegaai uit het geslacht Amazona, die overigens meer dan 30 soorten telt. Geen ander geslacht heeft zoveel soorten met zoveel overeenkomsten. Op het oog zijn ze soms zelfs niet voor experts te onderscheiden, zo zijn er in gevangenschap weinig zuivere amazones. - De geelvoorhoofdamazone is een in Noordelijk Zuid-Amerika en Midden-Amerika voorkomende vogel met een kenmerkend geel voorhoofd, een groen verenkleed en hier en daar rode, blauwe en gele veren over zijn lichaam. Het is een zeer kenmerkende soort, die bijna alleen met geelkop en dubbele geelkopamazones met weinig geel verward kan worden.', 1, 1, 1, 2, 14, 19, 41);
