INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (84, 'Zus', 1, 'Het gilamonster is makkelijk te herkennen; het lijf is zwart met rode tot roze vlekken, heeft een zeer wratachtige schubbenhuid, veroorzaakt door osteodermen, beenplaatjes die de huid versterken. De staart is bijna net zo dik als de kop en maximaal een derde van de totale lengte. De kleurpatronen verschillen omdat bij sommige soorten eerder sprake is van een roze achtergrond met zwarte vlekken, de ogen zitten enigszins verborgen. Er zijn twee ondersoorten: het gevlekte gilamonster H. suspectum suspectum en de meer gebandeerde H. suspectum cinctum. Het gilamonster heeft grof gebouwde klauwen, waarmee hij goed kan graven. De ogen, neusgaten en gehooropeningen zijn erg klein om verstoppingen te voorkomen. De dikke staart dient als vetopslag voor mindere tijden. De hagedis kan een maand zonder voedsel, water dient wel regelmatig opgenomen te worden.', 2, 1, 1, 4, 18, 28, 45);
INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (85, 'Zo', 0, 'Het gilamonster is makkelijk te herkennen; het lijf is zwart met rode tot roze vlekken, heeft een zeer wratachtige schubbenhuid, veroorzaakt door osteodermen, beenplaatjes die de huid versterken. De staart is bijna net zo dik als de kop en maximaal een derde van de totale lengte. De kleurpatronen verschillen omdat bij sommige soorten eerder sprake is van een roze achtergrond met zwarte vlekken, de ogen zitten enigszins verborgen. Er zijn twee ondersoorten: het gevlekte gilamonster H. suspectum suspectum en de meer gebandeerde H. suspectum cinctum. Het gilamonster heeft grof gebouwde klauwen, waarmee hij goed kan graven. De ogen, neusgaten en gehooropeningen zijn erg klein om verstoppingen te voorkomen. De dikke staart dient als vetopslag voor mindere tijden. De hagedis kan een maand zonder voedsel, water dient wel regelmatig opgenomen te worden.', 2, 1, 1, 4, 18, 28, 45);
INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (86, 'Jimmy', 0, 'De gitaarrog is wijd verspreid in de Indo-Pacific (Australië, Bangladesh, India, Indonesië, Maleisië, Singapore, Sri Lanka, Vietnam), maar toch kwetsbaar als soort. - Jonge gitaarroggen leven graag in mangroves met ondiep water. Eens volwassen, kiezen de dieren het ruime sop. Niettemin kan deze soort ook goed in zoet water leven. Deze roggen zien er uit als haaien en zwemmen met hun haaiachtige staart in plaats van te fladderen met hun borstvinnen, zoals de meeste roggen doen. Ze zijn niet gevaarlijk voor de mens. - Gitaarroggen jagen vooral op schaaldieren, met een voorkeur voor krabben en garnalen. Ze kunnen tot 4 meter groot worden!', 2, 1, 1, 3, 19, 29, 46);
INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (87, 'Jana', 1, 'Gnoes of wildebeesten zijn een geslacht van de evenhoevigen behorende tot de familie holhoornigen. - Het geslacht kent twee soorten, die leven op de grasvlakten van zuidelijk en oostelijk Afrika: Witstaartgnoe (Connochaetes gnou), Karoo, Zuid-Afrika en Blauwe gnoe (Connochaetes taurinus), Oost- tot Zuid-Afrika. - Beide soorten migreren in de droge tijd naar voedselrijke gebieden. Gnoes leggen duizenden kilometers per jaar af. Jongen kunnen al vlug mee met de kudde als ze geboren worden, soms wel al vanaf 10 minuten.', 1, 1, 1, 1, 3, 3, 47);
