INSERT INTO `ZooStart`.`Dieren` (`idDieren`, `Naam`, `Genus`, `Omschrijving`, `Status_id`, `Rijk_id`, `Stam_id`, `Klasse_id`, `Orde_id`, `Familie_id`, `Geslacht_id`) VALUES (95, 'Sophie', 1, 'De Grevy zebra (Equus grevyi) is een bedreigde zebra soort dat alleen in oost Afrika voor komt. Uit onderzoek blijkt dat de zebra zijn/haar strepen heeft voor als de kudde een roofdier ziet. De zebras beginnen dan door elkaar heen te rennen zodat het roofdier niet weet waar de zebra begint of eindigt. - Dit dier leeft in een harem met zowel hengsten als merries en met hun nageslacht. De hengsten worden door de leidende hengst uit de groep gejaagd als ze oud genoeg zijn. Over het algemeen valt de zebra veel ten prooi aan leeuwen, luipaarden en krokodillen maar worden in de meeste gevallen niet bedreigd. Helaas deze zebra soort wel.', 3, 1, 1, 1, 20, 31, 50);
